import React, {Component} from 'react';
import ListInput from './ListInput';
import './index.css';
import {list} from './list.js'
import ListItems from './ListItems';

class App extends Component { // jest to nowy syntax - nie trzeba tworzyć już konstruktora i używać super do dziedziczenia po obiekcie nadrzędnym. Wystarczy tak jak jest tutaj
    state = {
      inputField: '',
      listQueue: list
    }

  inputValue = (event) => {
    this.setState({inputField: event.target.value})
  }

  submitField = () => {
    this.setState({listQueue:list.push(this.state.inputField)}, // zmiana stanu listQueue na to co zostało wprowadzone w inputField - setState to funkcja asynchroniczna
    () => {console.log(list)}) // funkcja pokazująca  AKTUALNY STAN OBIEKTU listQueue i tablicy w nim zawartej - inaczej się nie da bo setState jest asynchroniczny.
    }

  render() {
    return(
      <div>
        <ListInput submit={this.submitField} input={this.inputValue}/>
        <ListItems  listitems={this.state.list}/>
      </div>
    )
  }
}

export default App;
