import React, {Component} from 'react';
import ListInput from './ListInput';
import './index.css';

class App extends Component { // jest to nowy syntax - nie trzeba tworzyć już konstruktora i używać super do dziedziczenia po obiekcie nadrzędnym. Wystarczy tak jak jest tutaj
    state = {
      inputField: '',
      list: []
    }

  inputValue = (event) => {
    this.setState({inputField: event.target.value}); // zmieniamy state input fielda na to co jest aktualnie wprowadzone w  polu input
    console.log(this.state.inputField);
  }

  submitField = () => {
    this.setState({list:[this.state.inputField.value]})
    // this.state.list.push(this.state.inputField); 
    console.log(this.state.list);

  }

  render() {
    return(
      <div>
        <ListInput submit={this.submitField} input={this.inputValue}/>
      </div>
    )
  }
}

export default App;
